// '../pages/exercise.js'

import React, { useState } from "react";
import { Container, SignupFormContainer, Title, InputField, Button } from "../styles/SignupStyle";

const Exercise = () => {
  const [date, setDate] = useState('');
  const [exercise, setExercise] = useState('');
  const [duration, setDuration] = useState('');
  const [exercises, setExercises] = useState([]);

  const handleAddExercise = (e) => {
    e.preventDefault();
    if (date && exercise && duration) {
      const newExercise = { date, exercise, duration };
      setExercises([...exercises, newExercise]);
      setDate('');
      setExercise('');
      setDuration('');
    }
  };

  return (
    <Container>
      <SignupFormContainer>
        <Title>운동량 조회</Title>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <InputField
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            placeholder="날짜"
          />
          <InputField
            type="text"
            value={exercise}
            onChange={(e) => setExercise(e.target.value)}
            placeholder="운동 종류"
          />
          <InputField
            type="number"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
            placeholder="운동 시간(분)"
          />
          <Button onClick={handleAddExercise}>운동 추가</Button>
        </div>
      </SignupFormContainer>
      {exercises.length > 0 && (
        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <Title>운동 기록</Title>
          <ul>
            {exercises.map((exercise, index) => (
              <li key={index}>
                {exercise.date} - {exercise.exercise} ({exercise.duration}분)
              </li>
            ))}
          </ul>
        </div>
      )}
    </Container>
  );
};

export default Exercise;
