// Exercise.js (React 컴포넌트)

import React, { useState } from "react";
import { Container, DataContainer, Title, DataEntry, IconContainer } from "../styles/ExerciseStyle";

const Exercise = () => {
  const [exerciseTime, setExerciseTime] = useState(0);
  const [exerciseDistance, setExerciseDistance] = useState(0);

  const fetchExerciseData = () => {
    const tempTime = 60;
    const tempDistance = 5.0;
    setExerciseTime(tempTime);
    setExerciseDistance(tempDistance);
  };

  React.useEffect(() => {
    fetchExerciseData();
  }, []);

  return (
    <Container>
      <Title>운동 데이터 조회</Title>
      <DataContainer>
        <DataEntry>
          <IconContainer><FaClock /></IconContainer>
          최근 운동 시간: {exerciseTime} 분
        </DataEntry>
        <DataEntry>
          <IconContainer><FaRunning /></IconContainer>
          최근 운동 거리: {exerciseDistance} km
        </DataEntry>
      </DataContainer>
    </Container>
  );
};

export default Exercise;
