import React from "react";
import { AppBar, Toolbar, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Link } from "react-router-dom";
import bannerImage from "../styles/banner.png"; // 배너 이미지 import

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  appBar: {
    backgroundColor: "#fff",
    borderBottom: "1px solid #ddd",
  },
  bannerImage: {
    height: 50, // 이미지 높이 조절 (필요에 따라 수정)
    margin : 40,
    marginRight: theme.spacing(2),
    cursor: "pointer", // 마우스 커서를 포인터로 변경하여 클릭 가능하게 함
  },
  button: {
    color: "#333",
  },
}));

const NavigationBar = () => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <AppBar position="static" className={classes.appBar}>
        <Toolbar>
          {/* 배너 이미지를 링크로 감싸서 클릭 이벤트 처리 */}
          <Link to="/home" className={classes.link}>
            <img src={bannerImage} alt="banner" className={classes.bannerImage} />
          </Link>


        </Toolbar>
      </AppBar>
    </div>
  );
};

export default NavigationBar;
