import React, { useState, useEffect } from "react";
import { Container, SignupFormContainer, Title } from "../styles/SignupStyle";
import Chart from "chart.js/auto";

const Exercise = () => {
  const [exerciseData, setExerciseData] = useState([
    { time: "2022-05-01 10:00", duration: 60 },
    { time: "2022-05-01 12:00", duration: 45 },
    { time: "2022-05-01 15:00", duration: 30 },
  ]);

  const fetchExerciseData = () => {
    // 실제 데이터 로직을 여기에 구현
    // 아두이노에서 데이터를 받아오거나 API를 호출하여 데이터를 가져올 수 있습니다.
    // 임시로 데이터를 업데이트하는 로직을 예시로 작성
    setTimeout(() => {
      const tempData = [
        { time: "2022-05-01 10:00", duration: 60 },
        { time: "2022-05-01 12:00", duration: 45 },
        { time: "2022-05-01 15:00", duration: 30 },
        { time: "2022-05-01 18:00", duration: 55 }, // 새로운 데이터
      ];
      setExerciseData(tempData);
    }, 2000); // 2초 후에 데이터를 업데이트 (임의의 시간으로 설정)
  };

  useEffect(() => {
    fetchExerciseData(); // 컴포넌트가 마운트된 후에 데이터를 가져오도록 설정
  }, []);

  useEffect(() => {
    const ctx = document.getElementById("exerciseChart");

    if (ctx && exerciseData.length > 0) {
      const chart = new Chart(ctx, {
        type: "line",
        data: {
          labels: exerciseData.map((data) => data.time),
          datasets: [{
            label: "운동 시간 (분)",
            data: exerciseData.map((data) => data.duration),
            fill: false,
            borderColor: "rgb(75, 192, 192)",
            tension: 0.1,
          }],
        },
        options: {
          scales: {
            x: {
              type: "time",
              time: {
                unit: "hour",
                displayFormats: {
                  hour: "YYYY-MM-DD HH:mm",
                },
              },
              title: {
                display: true,
                text: "시간",
              },
            },
            y: {
              beginAtZero: true,
              title: {
                display: true,
                text: "운동 시간 (분)",
              },
            },
          },
        },
      });

      return () => {
        chart.destroy(); // 컴포넌트가 언마운트될 때 차트를 정리합니다.
      };
    }
  }, [exerciseData]); // exerciseData가 변경될 때마다 이 useEffect가 실행됩니다.

  return (
    <Container>
      <SignupFormContainer>
        <Title>운동 데이터 조회 및 그래프</Title>
        <div>
          <canvas id="exerciseChart" width="400" height="200"></canvas>
        </div>
      </SignupFormContainer>
    </Container>
  );
};

export default Exercise;
