// Exercise.js (React 컴포넌트)

import React, { useState, useEffect } from "react";
import { Container, SignupFormContainer, Title } from "../styles/SignupStyle";
const SerialPort = require("serialport");

const Exercise = () => {
  const [exerciseTime, setExerciseTime] = useState(0);
  const [exerciseDistance, setExerciseDistance] = useState(0);

  useEffect(() => {
    const port = new SerialPort("COM3", { baudRate: 9600 }); // 아두이노 시리얼 포트에 맞게 수정
    const parser = port.pipe(new Readline({ delimiter: "\n" }));

    parser.on("data", (data) => {
      const [time, distance] = data.split(","); // 시간과 거리 데이터를 쉼표로 분리
      setExerciseTime(parseInt(time));
      setExerciseDistance(parseFloat(distance));
    });

    return () => {
      port.close(); // 컴포넌트 언마운트 시 포트 닫기
    };
  }, []);

  return (
    <Container>
      <SignupFormContainer>
        <Title>아두이노 운동 데이터 조회</Title>
        <div>
          <p>최근 운동 시간: {exerciseTime} 분</p>
          <p>최근 운동 거리: {exerciseDistance} km</p>
        </div>
      </SignupFormContainer>
    </Container>
  );
};

export default Exercise;
