import { Chart } from 'chart.js'; // 최신 버전에서는 이렇게 import

const Exercise = () => {
  const [exerciseData, setExerciseData] = useState([]);

  const fetchExerciseData = () => {
    const tempData = [
      { time: "2022-05-01T10:00:00", duration: 60 },
      { time: "2022-05-01T12:00:00", duration: 45 },
      { time: "2022-05-01T15:00:00", duration: 30 },
    ];
    setExerciseData(tempData);
  };

  useEffect(() => {
    const ctx = document.getElementById("exerciseChart");

    if (ctx) {
      const chart = new Chart(ctx, {
        type: "line",
        data: {
          labels: exerciseData.map((data) => data.time),
          datasets: [{
            label: "운동 시간 (분)",
            data: exerciseData.map((data) => data.duration),
            fill: false,
            borderColor: "rgb(75, 192, 192)",
            tension: 0.1,
          }],
        },
        options: {
          scales: {
            x: {
              type: "time",
              time: {
                unit: "hour",
                displayFormats: {
                  hour: "YYYY-MM-DD HH:mm",
                },
              },
              title: {
                display: true,
                text: "시간",
              },
            },
            y: {
              beginAtZero: true,
              title: {
                display: true,
                text: "운동 시간 (분)",
              },
            },
          },
        },
      });

      return () => {
        chart.destroy();
      };
    }
  }, [exerciseData]);

  useEffect(() => {
    fetchExerciseData();
  }, []); // 컴포넌트가 마운트된 후에 데이터를 가져옵니다.

  return (
    <Container>
      <SignupFormContainer>
        <Title>운동 데이터 조회 및 그래프</Title>
        <div>
          <canvas id="exerciseChart" width="400" height="200"></canvas>
        </div>
      </SignupFormContainer>
    </Container>
  );
};

export default Exercise;
