// Exercise.js (React 컴포넌트)

import React, { useState, useEffect } from "react";
import { Container, SignupFormContainer, Title } from "../styles/SignupStyle";
import Chart from "chart.js/auto";

const Exercise = () => {
  const [exerciseData, setExerciseData] = useState([]);

  // 임시 데이터 사용 (실제 아두이노 데이터 수신 부분은 생략)
  const fetchExerciseData = () => {
    // 아두이노에서 받아올 데이터 예시 (시간에 따른 운동량)
    const tempData = [
      { time: "2022-05-01 10:00", duration: 60 }, // 예시: 10시부터 60분 동안 운동
      { time: "2022-05-01 12:00", duration: 45 }, // 예시: 12시부터 45분 동안 운동
      { time: "2022-05-01 15:00", duration: 30 }, // 예시: 15시부터 30분 동안 운동
    ];
    setExerciseData(tempData);
  };

  useEffect(() => {
    fetchExerciseData(); // 임시 데이터를 받아오는 함수 호출

    // 차트 생성
    const ctx = document.getElementById("exerciseChart");
    const chart = new Chart(ctx, {
      type: "line",
      data: {
        labels: exerciseData.map((data) => data.time),
        datasets: [{
          label: "운동 시간 (분)",
          data: exerciseData.map((data) => data.duration),
          fill: false,
          borderColor: "rgb(75, 192, 192)",
          tension: 0.1,
        }],
      },
      options: {
        scales: {
          x: {
            type: "time",
            time: {
              unit: "hour",
              displayFormats: {
                hour: "yyyy-MM-dd HH:mm", // 수정된 부분
              },
            },
            title: {
              display: true,
              text: "시간",
            },
          },
          y: {
            beginAtZero: true,
            title: {
              display: true,
              text: "운동 시간 (분)",
            },
          },
        },
      },
    });



    return () => {
      chart.destroy(); // 컴포넌트 언마운트 시 차트 제거
    };
  }, [exerciseData]);

  return (
    <Container>
      <SignupFormContainer>
        <Title>운동 데이터 조회 및 그래프</Title>
        <div>
          <canvas id="exerciseChart" width="400" height="200"></canvas>
        </div>
      </SignupFormContainer>
    </Container>
  );
};

export default Exercise;
