import React, { useState, useEffect } from "react";
import { Container, SignupFormContainer, Title } from "../styles/SignupStyle";
import Chart from "chart.js/auto";

const Exercise = () => {
  const [exerciseData, setExerciseData] = useState([]);

  const fetchExerciseData = () => {
    const tempData = [
      { time: "2022-05-01 10:00", duration: 60 },
      { time: "2022-05-01 12:00", duration: 45 },
      { time: "2022-05-01 15:00", duration: 30 },
    ];
    setExerciseData(tempData);
  };

  useEffect(() => {
    fetchExerciseData();
  }, []); // 빈 배열을 전달하여 컴포넌트 마운트 시 한 번만 실행되도록 함

  useEffect(() => {
    if (exerciseData.length > 0) {
      const ctx = document.getElementById("exerciseChart");
      if (ctx) {
        const chart = new Chart(ctx, {
          type: "line",
          data: {
            labels: exerciseData.map((data) => data.time),
            datasets: [
              {
                label: "운동 시간 (분)",
                data: exerciseData.map((data) => data.duration),
                fill: false,
                borderColor: "rgb(75, 192, 192)",
                tension: 0.1,
              },
            ],
          },
          options: {
            scales: {
              x: {
                type: "time",
                time: {
                  unit: "hour",
                  displayFormats: {
                    hour: "YYYY-MM-DD HH:mm",
                  },
                },
                title: {
                  display: true,
                  text: "시간",
                },
              },
              y: {
                beginAtZero: true,
                title: {
                  display: true,
                  text: "운동 시간 (분)",
                },
              },
            },
          },
        });

        return () => {
          chart.destroy(); // 컴포넌트 언마운트 시 차트 제거
        };
      }
    }
  }, [exerciseData]); // exerciseData 상태에 의존하여 상태가 변경될 때마다 실행

  return (
    <Container>
      <SignupFormContainer>
        <Title>운동 데이터 조회 및 그래프</Title>
        <div>
          <canvas id="exerciseChart" width="400" height="200"></canvas>
        </div>
      </SignupFormContainer>
    </Container>
  );
};

export default Exercise;
