import React, { useState, useEffect, useRef } from "react";
import { Container, SignupFormContainer, Title } from "../styles/SignupStyle";
import Chart from "chart.js/auto";
import "chartjs-adapter-date-fns"; // Chart.js의 Date Adapter 추가
import exerciseImage from "../styles/exercise.png"; // 이미지 import

const Exercise = () => {
  const [exerciseData, setExerciseData] = useState([]);
  const chartRef = useRef(null); // 차트를 저장할 ref

  // 데이터를 불러오는 비동기 함수
  const fetchData = async () => {
    // 임시 데이터 (실제로는 API 호출 등으로 데이터를 가져와야 함)
    const tempData = [
      { time: "2022-05-01T10:00:00", duration: 60 },
      { time: "2022-05-02T10:00:00", duration: 40 },
      { time: "2022-05-03T10:00:00", duration: 30 },
      { time: "2022-05-04T10:00:00", duration: 50 },
    ];
    return tempData;
  };

  useEffect(() => {
    const fetchExerciseData = async () => {
      try {
        const response = await fetchData(); // 데이터를 불러오는 비동기 함수 호출
        setExerciseData(response); // 불러온 데이터를 상태에 저장
      } catch (error) {
        console.error("데이터 불러오기 오류:", error);
      }
    };

    fetchExerciseData(); // 데이터를 불러오는 함수 호출

  }, []); // 빈 배열을 전달하여 컴포넌트가 마운트될 때 한 번만 실행

  useEffect(() => {
    // exerciseData가 변경될 때마다 실행되는 useEffect
    if (exerciseData.length > 0) {
      if (chartRef.current) {
        // 이미 차트가 생성되어 있는 경우 차트를 업데이트
        chartRef.current.data.datasets[0].data = exerciseData.map((data) => data.duration);
        chartRef.current.update();
      } else {
        // 차트가 생성되지 않은 경우 새로운 차트 생성
        const ctx = document.getElementById("exerciseChart");
        chartRef.current = new Chart(ctx, {
          type: "line",
          data: {
            labels: exerciseData.map((data) => data.time),
            datasets: [{
              label: "운동 시간 (분)",
              data: exerciseData.map((data) => data.duration),
              fill: false,
              borderColor: "rgb(75, 192, 192)",
              tension: 0.1,
            }],
          },
          options: {
            scales: {
              x: {
                type: "time",
                time: {
                  unit: "day",
                  displayFormats: {
                    day: "MM-dd", // 날짜 형식 수정 (월-일)
                  },
                },
                title: {
                  display: true,
                  text: "날짜",
                },
              },
              y: {
                beginAtZero: true,
                title: {
                  display: true,
                  text: "운동 시간 (분)",
                },
              },
            },
          },
        });
      }
    }
  }, [exerciseData]); // exerciseData가 변경될 때마다 실행

  const calculateAchievementPercentage = () => {
    const totalExerciseMinutes = exerciseData.reduce((acc, cur) => acc + cur.duration, 0);
    const targetMinutes = 200; // 새로운 임의의 목표 시간 (예: 200분)
    const percentage = (totalExerciseMinutes / targetMinutes) * 100;
    return percentage.toFixed(2); // 소수점 2자리까지 표시
  };

  return (
    <Container>
      <SignupFormContainer>
        <Title>오늘의 활동량</Title>
        <div style={{ display: "flex", alignItems: "center", width: "270px" }}>
          <img src={exerciseImage} alt="exercise" style={{ width: 70, marginRight: 5 }} />
          {exerciseData.length > 0 ? (
            <>
              <canvas id="exerciseChart" width={400} height={200}></canvas>
            </>
          ) : (
            <p>데이터를 불러오는 중...</p>
          )}
        </div>
        {/* 목표 달성률 표시 코드 (그래프 아래에 표시) */}
        {exerciseData.length > 0 && (
          <>
            <p style={{ marginTop: 20 }}>목표 달성률: {calculateAchievementPercentage()}%</p>
            <canvas id="caloriesChart" width={200} height={200}></canvas>
          </>
        )}
      </SignupFormContainer>
    </Container>
  );
};

export default Exercise;
